
public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 4, y = 5, answer = 0;
		
		// Addition
		answer = x + y;
		System.out.println(answer);
		
		// Subtraction
		answer = x - y;
		System.out.println(answer);
		
		// Multiplication
		answer = x * y;
		System.out.println(answer);
		
		// Division
		answer = x / y;
		System.out.println(answer);
		
		// Modulus
		answer = x % y;
		System.out.println(answer);

	}

}
